import React from 'react';
import Task from '../task'
import { Modal } from 'antd';


class ToDoList extends React.Component {
  state = {
    task: {
      text: '',
      status: 'to-do'
    },
    id: 0,
    list: [],
    visible: false
  }


  deleteConfimation = () => {
    this.setState({ visible: true });
  }

  cancelDelete = () => {
    this.setState({ visible: false });
  };

  setTask = ({ target: { value } }) => {
    this.setState({ task: { text: value, status: 'to-do' } })

  }

  removeTask = (key) => {
    const list = [...this.state.list]
    list.splice(key, 1);

    this.setState({ list: list });
    //this.props.cancelDelete()
  }

  changeStatus = (id) => {
    const list = [...this.state.list]

    if (list[id].status === 'to-do') {
      list[id].status = 'done'
    } else {
      list[id].status = 'to-do'
    }
    this.setState({ list: list });
  }

  handleSubmit = (e) => {
    e.preventDefault();

    let { task, id } = this.state;
    task.id = id;
    let nextId = id + 1;


    let list = [...this.state.list];


    if (task.text.trim() === "" || task.text.length < 3) {
      return alert("Preencha o campo!")
    }

    list.push(task);

    this.setState({
      task: {
        text: '',
        status: "to-do"
      },
      id: nextId,
      list: list
    });
  }


  render() {
    console.log(this.state)

    const { list, task, visible } = this.state;

    return (
      <>

        <div>
          <Modal
            visible={visible}
            onOk={() => this.props.removeTask(this.props.id)}
            onCancel={this.cancelDelete}
            okText="Confirma"
            cancelText="Cancela"
          >
            <p>Tem certeza que deseja deletar esta tarefa?</p>
          </Modal>
        </div>

        <form onSubmit={this.handleSubmit}>
          <input value={task.text} onChange={this.setTask}></input>
          <button>Adicionar Task</button>
        </form>

        <div>
          <h3>Lista PendÃªncias</h3>
          {list.filter(task => task.status === 'to-do')
            .map((task, key) => (
              <Task
                task={task.text}
                key={key}
                id={task.id}
                removeTask={this.removeTask}
                status={task.status}
                changeStatus={this.changeStatus}
                deleteConfimation={this.deleteConfimation}
              //cancelDelete={this.cancelDelete.bind(this)}
              />
            ))}
        </div>
        <hr />
        <h3>Lista Feitas</h3>
        <div>
          {list.filter(task => task.status === 'done')
            .map((task, key) => (
              <Task
                task={task.text}
                key={key}
                id={task.id}
                removeTask={this.removeTask}
                status={task.status}
                changeStatus={this.changeStatus}
                deleteConfimation={this.deleteConfimation}
              //cancelDelete={this.cancelDelete.bind(this)}
              />
            ))}
        </div>
      </>

    )
  }
}

export default ToDoList;
