import React from 'react';
import { Checkbox } from 'antd';
import { DeleteOutlined } from '@ant-design/icons'




class Task extends React.Component {

  deleteConfimation = () => {
    alert("Você tem certeza que deseja apagar essa tarefa?")
  }
  render() {
    return (
      <Checkbox>
        {props.task}
        < button onClick={deleteConfimation} > <DeleteOutlined /></button >

      </Checkbox >
    );
  }
}

export default Task;
