import React from 'react';


class ToDoList extends React.Component {
  state = {
    listItem: '',
  }

  setListItem = ({ target: { value } }) => {
    this.setState({ listItem: value }, () => console.log(this.state))

  }


  render() {
    return (
      <input onChange={this.setListItem}></input>

    )
  }
}

export default ToDoList;
