import React from 'react';
import './index.css'
import { Checkbox, Modal } from 'antd';
import { DeleteOutlined } from '@ant-design/icons'


class Task extends React.Component {
  state = {
    visible: false
  }

  deleteConfimation = () => {
    this.setState({ visible: true });
  }

  cancelDelete = () => {
    this.setState({ visible: false });
  };

  check = (index) => {
    this.props.changeStatus(index)
  }


  render() {
    const { visible } = this.state;
    return (


      <>

        <div>
          <Modal
            visible={visible}
            onOk={() => this.props.removeTask(this.props.index)}
            onCancel={this.cancelDelete}
            okText="Confirma"
            cancelText="Cancela"
          >
            <p>Tem certeza que deseja deletar esta tarefa?</p>
          </Modal>
        </div>

        <div >
          <Checkbox
            checked={this.props.status === 'done'}
            onChange={() => this.check(this.props.index)}
          >
            <span className={this.props.status}>{this.props.task}{this.props.index}</span>
            < button onClick={this.deleteConfimation} > <DeleteOutlined /></button >
          </Checkbox >

        </div>
      </>
    );
  }
}

export default Task;
